export interface WelcomeVariables {
  userEmail: string;
  firstName: string;
  appName: string;
}

export function processWelcomeTemplate(variables: WelcomeVariables) {
  let html = welcomeHtml;
  let text = welcomeText;

  // Replace template variables with actual values
  for (const [key, value] of Object.entries(variables)) {
    // Convert camelCase to UPPERCASE_UNDERSCORE to match template placeholders
    const placeholderKey = key.replace(/([A-Z])/g, '_$1').toUpperCase();
    const placeholder = `{{${placeholderKey}}}`;
    html = html.replaceAll(placeholder, value);
    text = text.replaceAll(placeholder, value);
  }

  return { html, text };
}
